(function ($) {
    var BlogSlide = function ($scope, $) {
        var $carousel = $scope.find('.sunio-blog-slide');

        if ($carousel.length < 0) {
            return;
        }

        var carousel_wrap = $carousel.find('.owl-carousel');

        var $settings = carousel_wrap.data('settings');

        if (carousel_wrap.length <= 0) {
            return;
        }


        carousel_wrap.owlCarousel({
            items: $settings['slides_to_show'],
            margin: 20,
            autoplayHoverPause: true,
            nav: $settings['arrows'],
            navText: ["<i class='fa fa-chevron-left'></i>","<i class='fa fa-chevron-right'></i>"],
            dots: $settings['dots'],
            autoplay: $settings['autoplay'],
            responsive: {
                0: {
                    items: 1,
                    nav: true
                },
                480: {
                    items: 2,
                    nav: true
                },
                768: {
                    items: 2,
                    nav: true
                },
                1024: {
                    items: 3,
                    nav: true
                }
            }
        });


        // Make sure we run this code under Elementor

    };
    $(window).on('elementor/frontend/init', function () {
        elementorFrontend.hooks.addAction('frontend/element_ready/azt-blogslide.default', BlogSlide);
    });
})(jQuery);