( function( $ ) {
    var WidgetaztAccordionHandler = function( $scope, $ ) {

        var $carousel = $scope.find('.brand-lists');
        var    $settings 		= $carousel.data( 'settings' );

        $carousel.owlCarousel({
            items: $settings['slides_to_show'],
            margin: 10,
            autoplayHoverPause: $settings['pause_on_hover'],
            nav: $settings['arrows'],
            dots: $settings['dots'],
            autoplay: $settings['autoplay'],
            navText: ["<i class='fa fa-chevron-left'></i>","<i class='fa fa-chevron-right'></i>"],
            responsive:{
                0:{
                    items:2,
                },
                480:{
                    items: 2,
                },
                768:{
                    items:4,
                },
                1024:{
                    items:$settings['slides_to_show'],
                }

            }
        });
    }


    // Make sure we run this code under Elementor
    $( window ).on( 'elementor/frontend/init', function() {
        elementorFrontend.hooks.addAction( 'frontend/element_ready/azt-brands.default', WidgetaztAccordionHandler );
    } );
} )( jQuery );