( function( $ ) {

    var WooProduct = function( $scope, $ ) {
        var $carousel 	= $scope.find( '.sunio-single-product' );

        $carousel.owlCarousel({
            items: 1,
            autoplayHoverPause: true,
            nav: true,
            dots: false,
            autoPlay: 3000,
            navText: ["<i class='fa fa-chevron-left'></i>","<i class='fa fa-chevron-right'></i>"],

            responsive:{
                0:{
                    items:1,
                },
                480:{
                    items: 1,
                },
                768:{
                    items:1,
                },
                1024:{
                    items: 1
                }

            }
        });

        var $carousel2 = $scope.find('.product-thumnails .owl-carousel');
        $carousel2.owlCarousel({
            items: 5,
            margin: 10,
            autoplayHoverPause: true,
            nav: true,
            dots: false,
            autoPlay: 3000,
            navText: ["<i class='fa fa-chevron-left'></i>","<i class='fa fa-chevron-right'></i>"],

            responsive:{
                0:{
                    items:5,
                },
                480:{
                    items: 5,
                },
                768:{
                    items:5,
                },
                1024:{
                    items: 5
                }

            }
        });

    };

    // Make sure we run this code under Elementor
    $( window ).on( 'elementor/frontend/init', function() {
        elementorFrontend.hooks.addAction( 'frontend/element_ready/azt-woo_single_product.default', WooProduct );
    } );
} )( jQuery );